import './App.css';
import ShortUrl from './ShortUrl';

function App() {
  function getrandom() {
    var text = "";
    var possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    for (var i = 0; i < 5; i++)
      text += possible.charAt(Math.floor(Math.random() * possible.length));
    return text;
  }

  function geturl() {
    var newurl = ""
    var url = document.getElementById("urlinput").value;
    var protocol_ok = url.startsWith("http://") || url.startsWith("https://");
    if (!protocol_ok) {
      newurl = "http://" + url;
      return newurl;
    } else {
      return url;
    }
  }

  function genhash() {
    if (window.location.hash === "") {
      window.location.hash = getrandom();
    }
  }

  function shorturl(){
    var longurl = geturl();
    genhash();

    // send_request(longurl);
  }

  return (
    <>
      <nav className='nav'>
        <div className='logo'>GREEN TEA</div>
        <div className='nav-link'>
          <p>Login</p>
        </div>
      </nav>
      <section className='container'>
        <h1>Short URL</h1>
        <form method='get' action='#'>
          <input type='text' placeholder='Enter URL to make short' name='URL' id='urlinput'></input>
          <button type='submit'>Shorten URL</button>
        </form>
        <ShortUrl></ShortUrl>
      </section>
      <footer>
        <p>Made by Me❤️</p>
      </footer>
    </>
  );
}

export default App;
