import CopyToClipboard from 'react-copy-to-clipboard';

export default function ShortUrl() {
    var shortUrl = 'https://create-react-app.dev/'

    return (
        <div className='shortUrl'>
            {/* show url and qr code*/}
            <p></p>
            <CopyToClipboard text={shortUrl}>
                <button>Copy</button>
            </CopyToClipboard>
        </div>
    );
}
