import './App.css';
import CopyToClipboard from 'react-copy-to-clipboard';

function App() {
  var shortUrl = 'https://create-react-app.dev/'

  return (
    <>
      <nav className='nav'>
        <div className='logo'>LOGO</div>
        <div className='nav-link'>
          <p>Login</p>
        </div>
      </nav>
      <section className='container'>
        <form method='get' action='#'>
          <input type='text' placeholder='Enter your URL to make Short' name='URL'></input>
          <button type='submit'>Generate</button>
          {/* show url and qr code*/}
        </form>
        <div className='shortUrl'>
            <CopyToClipboard text={shortUrl}>
              <input value={shortUrl} readOnly></input>
              <button>Copy</button>
            </CopyToClipboard>
          </div>
      </section>
    </>
  );
}

export default App;
